import React, { Component } from 'react'
import { MDBContainer, MDBCard, MDBCardBody, MDBIcon, MDBInput, MDBBtn } from 'mdbreact'
import Navbar from '../components/share/Navbar'
import { connect } from 'react-redux'
import { io } from "socket.io-client"
import { Marshalling, UnMarshalling } from '../Redux/Actions/MarshalService'

//Always instantiate this


class Deposit extends Component {
    state = {
        accountAmount: 0,
        timeoutRetransmit: true,
    }
    Deposit = () => {
        const socket = io('http://localhost:2222/', { transports: ['websocket'] })
        let sendingData = {
            AccountNo: this.state.AccountNo,
            AccName: this.state.AccName,
            Password: this.state.Password,
            Currency: this.state.Currency,
            Amount: parseFloat(this.state.Amount),
            Mode: 1
        }
        let marshallData = Marshalling(sendingData)
        socket.emit('deposit', marshallData)

        socket.on('deposit-reply', (data) => {
            data = UnMarshalling(data)
            data = data['Server-Response'][0]
            alert("Your new Balance is: " + data.Balance)
            this.Back()
        })
    }

    Back = () => {
        this.props.history.push('/home')
    }

    handleChange = (e) => {
        this.setState({
            [e.target.id]: e.target.value
        })
    }
    render() {
        return (
            <div>
                <Navbar /><br />
                <MDBContainer>
                    <MDBCard id="classic-card">
                        <MDBCardBody className="black-text">
                            <h3 className="text-center">
                                <MDBIcon icon="user" /> Deposit:
                            </h3>
                            <hr className="hr-light" />
                            <MDBInput
                                className="black-text"
                                iconClass="black-text"
                                label="Account No"
                                icon="envelope"
                                id="AccountNo"
                                type="number"
                                value={this.state.AccountNo}
                                onChange={this.handleChange}
                            />
                            <MDBInput
                                className="black-text"
                                iconClass="black-text"
                                label="Account Name"
                                icon="user"
                                id="AccName"
                                type="text"
                                value={this.state.AccName}
                                onChange={this.handleChange}
                            />
                            <MDBInput
                                className="black-text"
                                iconClass="black-text"
                                label="Password"
                                icon="lock"
                                id="Password"
                                type="password"
                                value={this.state.Password}
                                onChange={this.handleChange}
                            />
                            <MDBInput
                                className="black-text"
                                iconClass="black-text"
                                label="SGD/MYR/KRW"
                                icon="money-bill"
                                id="Currency"
                                type="text"
                                value={this.state.Currency}
                                onChange={this.handleChange}
                            />
                            <MDBInput
                                className="black-text"
                                iconClass="black-text"
                                label="Deposit Amount"
                                icon="dollar-sign"
                                type="number"
                                id="Amount"
                                value={this.state.Amount}
                                onChange={this.handleChange}
                            />
                            <div className="text-center mt-4 black-text">
                                <MDBBtn color="dark-green" onClick={this.Deposit}> Deposit
                                </MDBBtn>

                                <MDBBtn color="white" onClick={this.Back} > Back
                                </MDBBtn>
                                <hr className="hr-light" />
                            </div>
                        </MDBCardBody>
                    </MDBCard>
                </MDBContainer>
            </div>
        )
    }
}
const mapStateToProps = state => ({});
export default connect(mapStateToProps,)(Deposit)
